-- ----------------------------
--  Table structure for `incidents`
-- ----------------------------
DROP TABLE IF EXISTS `incidents`;
CREATE TABLE `incidents` (
  `id` varchar(255) NOT NULL,
  `caseNum` varchar(255) DEFAULT NULL,
  `summary` text,
  `date` date DEFAULT NULL,
  `location` varchar(255) DEFAULT NULL,
  `city` varchar(255) DEFAULT NULL,
  `zip` varchar(255) DEFAULT NULL,
  `injuryLevel` varchar(255) DEFAULT NULL,
  `manner` varchar(255) DEFAULT NULL,
  `shotsFired` int(11) DEFAULT NULL,
  `shotsHit` int(11) DEFAULT NULL,
  `copsFired` int(11) DEFAULT NULL,
  `suspectsInvolved` varchar(255) DEFAULT NULL,
  `initialContact` varchar(255) DEFAULT NULL,
  `callType` varchar(255) DEFAULT NULL,
  `isDomesticDispute` tinyint(1) DEFAULT NULL,
  `lessLethalForceUsed` varchar(255) DEFAULT NULL,
  `isSuicideByCop` tinyint(1) DEFAULT NULL,
  `shotIntoVehicle` tinyint(1) DEFAULT NULL,
  `isAccidental` tinyint(1) DEFAULT NULL,
  `footPursuit` tinyint(1) DEFAULT NULL,
  `carPursuit` tinyint(1) DEFAULT NULL,
  `physicalStruggle` tinyint(1) DEFAULT NULL,
  `lawsuitFiled` tinyint(1) DEFAULT NULL,
  `hasDiscrepancies` tinyint(1) DEFAULT NULL,
  `isModelCase` tinyint(1) DEFAULT NULL,
  `notes` text,
  `reviewBoard` tinyint(1) DEFAULT NULL,
  `otherOfficersInjured` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

