-- ----------------------------
--  Procedure structure for `Count_ALL_ShootingsByYear`
-- ----------------------------
DROP PROCEDURE IF EXISTS `Count_ALL_ShootingsByYear`;
delimiter ;;
CREATE DEFINER=`kavya`@`localhost` PROCEDURE `Count_ALL_ShootingsByYear`()
BEGIN
	SELECT
		YEAR (date) AS YEAR,
		count(*) AS Count
	FROM
		incidents
	GROUP BY 	YEAR
	ORDER BY	YEAR;
END
 ;;
delimiter ;

-- ----------------------------
--  Procedure structure for `Count_ALL_ShootingsByYearAndAgency`
-- ----------------------------
DROP PROCEDURE IF EXISTS `Count_ALL_ShootingsByYearAndAgency`;
delimiter ;;
CREATE DEFINER=`kavya`@`localhost` PROCEDURE `Count_ALL_ShootingsByYearAndAgency`()
BEGIN
	SELECT
		YEAR (I.date) AS YEAR,
		O.Agency,
		count(DISTINCT I.id) as Count
	FROM
		officerIncidentMap O
	INNER JOIN incidents I ON O.incidentId = I.id	
	GROUP BY
		YEAR,
		O.Agency
	ORDER BY
		YEAR, O.Agency;
END
 ;;
delimiter ;

-- ----------------------------
--  Procedure structure for `Count_ALL_SubjectsByAgency`
-- ----------------------------
DROP PROCEDURE IF EXISTS `Count_ALL_SubjectsByAgency`;
delimiter ;;
CREATE DEFINER=`kavya`@`localhost` PROCEDURE `Count_ALL_SubjectsByAgency`()
BEGIN
	SELECT
	O.agency,
	Count(DISTINCT S.subjectId) AS subjectsShotAt
	FROM
		incidentsubjectmap S
	INNER JOIN incidents I ON I.id = S.incidentId
	INNER JOIN officerincidentmap O ON O.incidentId = I.id
	GROUP BY
		O.agency
	ORDER BY
		subjectsShotAt DESC;
END
 ;;
delimiter ;

-- ----------------------------
--  Procedure structure for `Count_ALL_SubjectsWhoShotAtCopsByRaceAgency`
-- ----------------------------
DROP PROCEDURE IF EXISTS `Count_ALL_SubjectsWhoShotAtCopsByRaceAgency`;
delimiter ;;
CREATE DEFINER=`kavya`@`localhost` PROCEDURE `Count_ALL_SubjectsWhoShotAtCopsByRaceAgency`()
BEGIN
	SELECT
	officerincidentmap.agency,
	subjects.ethnicity,
	COUNT(DISTINCT subjects.Id) AS subjectcount
	FROM
		incidentsubjectmap
	INNER JOIN subjects ON incidentsubjectmap.subjectId = subjects.Id
	INNER JOIN officerincidentmap ON incidentsubjectmap.incidentId = officerincidentmap.incidentId
	WHERE
		incidentsubjectmap.shotAtPolice = TRUE
	GROUP BY
		officerincidentmap.agency,
		subjects.ethnicity
	ORDER BY
		agency;
END
 ;;
delimiter ;

-- ----------------------------
--  Procedure structure for `Count_ALL_SubjectsYearlyByDispositionPerAgency`
-- ----------------------------
DROP PROCEDURE IF EXISTS `Count_ALL_SubjectsYearlyByDispositionPerAgency`;
delimiter ;;
CREATE DEFINER=`kavya`@`localhost` PROCEDURE `Count_ALL_SubjectsYearlyByDispositionPerAgency`(IN agency_var varchar(200))
BEGIN
	SELECT
	O.agency,
	Year(I.date) as Year,
	S.Conviction,
	Count(DISTINCT S.subjectId) AS subjectsShotAt
	FROM
		incidentsubjectmap S
	INNER JOIN incidents I ON I.id = S.incidentId
	INNER JOIN officerincidentmap O ON O.incidentId = I.id
	WHERE O.agency = agency_var
	GROUP BY
		Year, S.Conviction
	ORDER BY
		Year(I.date);
END
 ;;
delimiter ;

